// Transform R700 RFID data
// Extract relevant fields from R700 reader data

// Validate input payload
if (!msg.payload || typeof msg.payload !== 'object') {
    msg.statusCode = 400;
    msg.payload = { error: 'Invalid payload: expected object' };
    return msg;
}

var transformed = {
    timestamp: new Date().toISOString(),
    reader_id: msg.payload.reader_id || 'unknown',
    tags: []
};

if (msg.payload.tags && Array.isArray(msg.payload.tags)) {
    transformed.tags = msg.payload.tags.map(tag => ({
        epc: tag.epc || '',
        rssi: tag.rssi !== undefined ? tag.rssi : null,
        antenna: tag.antenna !== undefined ? tag.antenna : null,
        read_count: tag.read_count !== undefined ? tag.read_count : null,
        first_seen: tag.first_seen || transformed.timestamp,
        last_seen: tag.last_seen || transformed.timestamp
    }));
}

msg.payload = transformed;
return msg;
